import {
  menuListApi, menuCreateApi, menuUpdateApi, menuDeleteApi
} from '@/api/system'
export default {
  name: 'Menu',
  data() {
    return {
      isChecked: false,
      listLoading: true,
      tableData: {
        data: [],
        total: 0
      },
      tableFrom: {
        page: 1,
        limit: 20
      }
    }
  },
  mounted() {
    this.getList()
  },
  methods: {
    // 列表
    getList() {
      this.listLoading = true
      menuListApi(this.tableFrom).then(res => {
        this.tableData.data = res.data.list
        this.tableData.total = res.data.count
        this.listLoading = false
      }).catch(res => {
        this.listLoading = false
        this.$message.error(res.message)
      })
    },
    pageChange(page) {
      this.tableData.page = page
      this.getList()
    },
    handleSizeChange(val) {
      this.tableData.limit = val
      this.getList()
    },
    // 添加
    onAdd(id) {
      const config = {}
      if (Number(id) > 0) config.formData = { pid: id }
      this.$modalForm(menuCreateApi(), config).then(() => {
        this.getList()
        this.$store.dispatch('user/getMenus')
      })
    },
    // 编辑
    onEdit(id) {
      this.$modalForm(menuUpdateApi(id)).then(() => {
        this.getList()
        this.$store.dispatch('user/getMenus')
      })
    },
    // 删除
    handleDelete(id, idx) {
      this.$modalSure('删除菜单吗').then(() => {
        menuDeleteApi(id).then(({ message }) => {
          this.$message.success(message)
          this.getList()
        }).catch(({ message }) => {
          this.$message.error(message)
        })
      })
    }
  }
}
