import { merchantRateApi } from '@/api/home'
import echartsFrom from '@/components/echarts/index'
import { fromList } from '@/libs/constants.js'
export default {
  name: 'MerchantRate',
  components: { echartsFrom },
  data() {
    return {
      style: { height: '281px' },
      merchantRate: [],
      optionData: {},
      fromList: fromList,
      name: '最近30天'
    }
  },
  mounted() {
    this.getList('lately30')
  },
  methods: {
    setTime(val, text) {
      this.name = text
      this.getList(val)
    },
    getList(val) {
      merchantRateApi({ date: val }).then(res => {
        if(res.status === 200){
            this.merchantRate = res.data.list
            const legend = []
            const series = []
            const colors = ['#5AD8A6', '#5B8FF9', '#F6BD16', '#5D7092', '#C6C6C6']
            this.merchantRate.map((item, i) => {
            legend.push(item.category_name)
            series.push({ value: item.pay_price, name: item.category_name, itemStyle: { normal: { color: colors[i] }}})
            })
            this.optionData = {
            tooltip: {
                trigger: 'item',
                formatter: '{a} <br/>{b} : {c} ({d}%)'
            },
            legend: {
                orient: 'vertical',
                left: 'left',
                data: legend
            },
            series: [
                {
                name: '访问来源',
                type: 'pie',
                radius: '65%',
                center: ['60%', '65%'],
                data: series,
                emphasis: {
                    itemStyle: {
                    shadowBlur: 10,
                    shadowOffsetX: 0,
                    shadowColor: 'rgba(0, 0, 0, 0.5)'
                    }
                }
                }
            ]
            }
            }
      }).catch(res => {
        this.$message.error(res.message)
      })
    }
  }
}
