import { smsNumberApi, smsPriceApi, payCodeApi } from '@/api/sms'
import { isLogin } from '@/libs/public'
import { roterPre } from '@/settings'
import { mapGetters } from 'vuex'
export default {
  name: 'SmsPay',
  data() {
    return {
      numbers: '',
      account: '',
      list: [],
      current: 0,
      checkList: {},
      fullscreenLoading: false,
      code: {}
    }
  },
  computed: {
    ...mapGetters([
      'isLogin'
    ])
  },
  mounted() {
    if (!this.isLogin) {
      // this.$message.warning('请先登录')
      this.$router.push(roterPre + '/sms/config/?url=' + this.$route.path)
    } else {
      this.getNumber()
      this.getPrice()
    }

    // this.onIsLogin()
  },
  methods: {
    // 查看是否登录
    onIsLogin() {
      this.fullscreenLoading = true
      this.$store.dispatch('user/isLogin').then(async res => {
        const data = res.data
        if (!data.status) {
          this.$message.warning('请先登录')
          this.$router.push(roterPre + '/sms/config/?url=' + this.$route.path)
        } else {
          this.getNumber()
          this.getPrice()
        }
        this.fullscreenLoading = false
      }).catch(res => {
        this.$message.error(res.message)
        this.$router.push(roterPre + '/sms/config?url=' + this.$route.path)
        this.fullscreenLoading = false
      })
    },
    // 剩余条数
    getNumber() {
      smsNumberApi().then(async res => {
        const data = res.data
        this.numbers = data.number
        this.account = data.account
      }).catch(res => {
        this.$message.error(res.message)
      })
    },
    // 支付套餐
    getPrice() {
      this.fullscreenLoading = true
      smsPriceApi().then(async res => {
        setTimeout(() => {
          this.fullscreenLoading = false
        }, 800)
        const data = res.data
        this.list = data.data
        this.checkList = this.list[0]
        this.getCode(this.checkList)
      }).catch(res => {
        this.fullscreenLoading = false
        this.$message.error(res.message)
      })
    },
    // 选中
    check(item, index) {
      this.fullscreenLoading = true
      this.current = index
      setTimeout(() => {
        this.getCode(item)
        this.checkList = item
        this.fullscreenLoading = false
      }, 800)
    },
    // 支付码
    getCode(item) {
      const data = {
        payType: 'weixin',
        mealId: item.id,
        price: item.price
      }
      payCodeApi(data).then(async res => {
        this.code = res.data
      }).catch(res => {
        this.$message.error(res.message)
      })
    }
  }
}
