import {
  seckillChangeApi,
  seckillProductLstApi,
  seckillProductDetailApi,
  categoryListApi,
  seckillProductUpdateApi,
  seckillLstFilterApi,
  seckillMerSelectApi,
  seckillProductOffApi
} from '@/api/product'
import { roterPre } from '@/settings'
import infoFrom from './info'
import ueditorFrom from '@/components/ueditorFrom'
const proOptions = [
  { name: '热门榜单', value: 'is_hot' },
  { name: '促销单品', value: 'is_benefit' },
  { name: '精品推荐', value: 'is_best' },
  { name: '优品推荐', value: 'is_good' }
]
export default {
  name: 'ProductExamine1',
  components: { infoFrom, ueditorFrom },
  data() {
    return {
      ruleValidate: {},
      dialogVisible: false,
      checkboxGroup: [],
      recommend: proOptions,
      formValidate: {
        is_hot: 0,
        is_best: 0,
        is_new: 0,
        is_benefit: 0,
        ficti: 1,
        content: '',
        store_name: ''
      },
       seckillStatusList: [
        { label: "正在进行", value: 2 },
        { label: "活动已结束", value: 3 },
        { label: "活动未开始", value: 1 },
      ],
      fullscreenLoading: false,
      isShow: false,
      roterPre: roterPre,
      listLoading: true,
      tableData: {
        data: [],
        total: 0
      },
      tableFrom: {
        page: 1,
        limit: 20,
        cate_id: '',
        store_name: '',
        type: '6',
        mer_id: '',
        seckill_status: "",
        keyword: '',
        is_trader: ''
      },
      categoryList: [],
      merCateList: [],
      multipleSelection: [],
      headeNum: [],
      merSelect: [],
      OffId: [],
      productId: 0
    }
  },
  mounted() {
    this.getMerSelect()
    this.getList('')
    this.getCategorySelect()
    this.getLstFilterApi()
  },
  methods: {
    subSuccess() {
      this.getList('')
      this.getLstFilterApi()
    },
    onchangeIsShow(row) {
      seckillChangeApi(row.product_id, row.is_used)
        .then(({ message }) => {
          this.$message.success(message)
          this.getList('')
        })
        .catch(({ message }) => {
          this.$message.error(message)
        })
    },
    getInfo(id) {
      this.fullscreenLoading = true
      seckillProductDetailApi(id)
        .then((res) => {
          const info = res.data
          this.formValidate = {
            is_hot: info.is_hot,
            is_best: info.is_best,
            is_new: info.is_new,
            is_benefit: info.is_benefit,
            ficti: info.ficti,
            content: info.content,
            store_name: info.store_name,
            rank: info.rank
          }
          if (info.is_good === 1) this.checkboxGroup.push('is_good')
          if (info.is_benefit === 1) this.checkboxGroup.push('is_benefit')
          if (info.is_best === 1) this.checkboxGroup.push('is_best')
          if (info.is_new === 1) this.checkboxGroup.push('is_new')
          this.fullscreenLoading = false
        })
        .catch((res) => {
          this.$message.error(res.message)
          this.fullscreenLoading = false
        })
    },
    onEdit(id) {
      this.productId = id
      this.getInfo(id)
      this.dialogVisible = true
    },
    // 提交
    handleSubmit(name) {
      this.onChangeGroup()
      this.$refs[name].validate((valid) => {
        if (valid) {
          seckillProductUpdateApi(this.productId, this.formValidate)
            .then(async(res) => {
              this.fullscreenLoading = false
              this.$message.success(res.message)
              this.dialogVisible = false
              this.getList('')
            })
            .catch((res) => {
              this.fullscreenLoading = false
              this.$message.error(res.message)
            })
        } else {
          return false
        }
      })
    },
    onChangeGroup() {
      this.checkboxGroup.includes('is_benefit')
        ? (this.formValidate.is_benefit = 1)
        : (this.formValidate.is_benefit = 0)
      this.checkboxGroup.includes('is_best')
        ? (this.formValidate.is_best = 1)
        : (this.formValidate.is_best = 0)
      this.checkboxGroup.includes('is_new')
        ? (this.formValidate.is_new = 1)
        : (this.formValidate.is_new = 0)
      this.checkboxGroup.includes('is_hot')
        ? (this.formValidate.is_hot = 1)
        : (this.formValidate.is_hot = 0)
    },
    handleClose() {
      this.dialogVisible = false
    },
    // 批量下架
    batchOff() {
      if (this.multipleSelection.length === 0) { return this.$message.warning('请先选择商品') }
      this.toOff(this.OffId)
    },
    // 下架
    toOff(id) {
      this.$prompt('强制下架', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        inputErrorMessage: '请输入强制下架原因',
        inputType: 'textarea',
        inputPlaceholder: '请输入强制下架原因',
        inputValidator: (value) => {
          if (!value) {
            return '请输入强制下架原因'
          }
        }
      })
        .then(({ value }) => {
          seckillProductOffApi({ id: id, status: -2, refusal: value })
            .then((res) => {
              this.$message({
                type: 'success',
                message: '提交成功'
              })
              this.getLstFilterApi()
              this.getList('')
            })
            .catch((res) => {
              this.$message.error(res.message)
            })
        })
        .catch(() => {
          this.$message({
            type: 'info',
            message: '取消输入'
          })
        })
    },
    // 列表表头；
    getLstFilterApi() {
      seckillLstFilterApi()
        .then((res) => {
          this.headeNum = res.data
        })
        .catch((res) => {
          this.$message.error(res.message)
        })
    },
    // 商户列表；
    getMerSelect() {
      seckillMerSelectApi()
        .then((res) => {
          this.merSelect = res.data
        })
        .catch((res) => {
          this.$message.error(res.message)
        })
    },
    batch() {
      if (this.multipleSelection.length === 0) { return this.$message.warning('请先选择商品') }
      this.$refs.infoFrom.dialogVisible = true
      this.isShow = false
    },
    handleSelectionChange(val) {
      this.multipleSelection = val
      const data = []
      this.multipleSelection.map((item) => {
        data.push(item.product_id)
      })
      this.OffId = data
    },
    toExamine(id) {
      this.$refs.infoFrom.dialogVisible = true
      this.isShow = true
      this.$refs.infoFrom.getInfo(id)
    },
    // 商户分类；
    getCategorySelect() {
      categoryListApi()
        .then((res) => {
          this.merCateList = res.data
        })
        .catch((res) => {
          this.$message.error(res.message)
        })
    },
    // 列表
    getList(num) {
      this.listLoading = true
      this.tableFrom.page = num ? num : this.tableFrom.page;
      seckillProductLstApi(this.tableFrom)
        .then((res) => {
          this.tableData.data = res.data.list
          this.tableData.total = res.data.count
          this.listLoading = false
        })
        .catch((res) => {
          this.listLoading = false
          this.$message.error(res.message)
        })
    },
    pageChange(page) {
      this.tableFrom.page = page
      this.getList('')
    },
    handleSizeChange(val) {
      this.tableFrom.limit = val

      this.getList(1)
    }
    // 删除
    // handleDelete(id, idx) {
    //   this.$modalSure().then(() => {
    //     productDeleteApi(id).then(({ message }) => {
    //       this.$message.success(message)
    //       this.getList()
    //     }).catch(({ message }) => {
    //       this.$message.error(message)
    //     })
    //   })
    // },
  }
}
