import {
  spikeConfigLstApi,
  spikeConfigDeleteApi,
  spikeConfigurationApi,
  spikeConfigUpdateApi,
  spikeConfigStatusApi
} from '@/api/marketing'
import { roterPre } from '@/settings'
export default {
  name: 'CouponList',
  data() {
    return {
      Loading: false,
      dialogVisible: false,
      roterPre: roterPre,
      listLoading: true,
      tableData: {
        data: [],
        total: 0
      },
      tableFrom: {
        page: 1,
        limit: 20,
        status: ""
      },
      tableFromIssue: {
        page: 1,
        limit: 10,
        coupon_id: 0
      },
      issueData: {
        data: [],
        total: 0
      }
    }
  },
  mounted() {
    this.getList()
  },
  methods: {
    // 添加秒杀配置
    addSpike() {
      this.$modalForm(spikeConfigurationApi().then()).then(() =>
        this.getList()
      )
    },
    // 编辑
    handleEdit(id) {
      this.$modalForm(spikeConfigUpdateApi(id).then()).then(() =>
        this.getList()
      )
    },
    // 删除
    handleDelete(id, idx) {
      this.$modalSure().then(() => {
        spikeConfigDeleteApi(id)
          .then(({ message }) => {
            this.$message.success(message)
            this.tableData.data.splice(idx, 1)
          })
          .catch(({ message }) => {
            this.$message.error(message)
          })
      })
    },
    handleClose() {
      this.dialogVisible = false
    },
    // 列表
    getList() {
      this.listLoading = true
      console.log(this.tableFrom)
      spikeConfigLstApi(this.tableFrom)
        .then((res) => {
          this.tableData.data = res.data.list
          this.tableData.total = res.data.count
          this.listLoading = false
        })
        .catch((res) => {
          this.listLoading = false
          this.$message.error(res.message)
        })
    },
    pageChange(page) {
      this.tableFrom.page = page
      this.getList()
    },
    handleSizeChange(val) {
      this.tableFrom.limit = val
      this.getList()
    },
    // 修改状态
    onchangeIsShow(row) {
      spikeConfigStatusApi(row.seckill_time_id, row.status )
        .then(({ message }) => {
          this.$message.success(message)
          this.getList()
        })
        .catch(({ message }) => {
          this.$message.error(message)
        })
    }
  }
}
