import { smsTempLstApi, tempCreateApi } from '@/api/sms'
import { roterPre } from '@/settings'
import { mapGetters } from 'vuex'
export default {
  name: 'SmsTemplate',
  filters: {
    statusFilter(status) {
      const statusMap = {
        0: '没有',
        1: '有'
      }
      return statusMap[status]
    }
  },
  data() {
    return {
      fullscreenLoading: false,
      listLoading: false,
      dialogVisible: false,
      tableData: {
        data: [],
        total: 0
      },
      form: {
        title: '',
        content: '',
        type: 1
      },
      placeholder: {},
      placeholderObj:[
        {
          title: '例如：注册验证码',
          content: '例如：您的验证码是：1170，有效期为1分钟。如非本人操作，可不用理会'
        },
        {
          title: '例如：订单支付成功通知',
          content: '例如 ：您购买的商品已支付成功，支付金额******元，订单号wx15949****6185920,感谢光临！'
        },
        {
          title: '例如：国庆优惠活动',
          content: '10月1日-8日期间 全场商品满1000减200，国庆欢乐购，立即查看http://mer.crmeb.net/admin.'
        }
      ],
      tableFrom: {
        page: 1,
        limit: 20,
        status: '',
        temp_type: '',
        title: ''
      }
    }
  },
  computed: {
    ...mapGetters([
      'isLogin'
    ])
  },
  mounted() {
    console.log(this.isLogin);
    if (!this.isLogin) {
      this.$router.push(roterPre + '/sms/config?url=' + this.$route.path)
    } else {
      this.getList()
    }
    this.changeRadioText();
  },
  methods: {

    changeRadioText(){
      this.placeholder=this.placeholderObj[this.form.type-1]
    },
    // 查看是否登录
    onIsLogin() {
      this.fullscreenLoading = true
      this.$store.dispatch('user/isLogin').then(async res => {
        const data = res.data
        if (!data.status) {
          this.$message.warning('请先登录')
          this.$router.push(roterPre + '/sms/config?url=' + this.$route.path)
        } else {
          this.getList()
        }
        this.fullscreenLoading = false
      }).catch(res => {
        this.$message.error(res.message)
        this.$router.push(roterPre + '/sms/config?url=' + this.$route.path)
        this.fullscreenLoading = false
      })
    },
    // 列表
    getList() {
      this.listLoading = true
      smsTempLstApi(this.tableFrom).then(res => {
        this.tableData.data = res.data.data
        this.tableData.total = res.data.count
        this.listLoading = false
      }).catch(res => {
        this.listLoading = false
        this.$message.error(res.message)
      })
    },
    pageChange(page) {
      this.tableFrom.page = page
      this.getList()
    },
    handleSizeChange(val) {
      this.tableFrom.limit = val
      this.getList()
    },
    // 表格搜索
    userSearchs() {
      this.getList()
    },
    // 申请
    apply(){
      this.form={title: '',content: '',type: 1};
      this.dialogVisible = true
    },
    // 添加
    add() {
      /*this.$modalForm(tempCreateApi()).then(() => this.getList())*/
      if(!this.form.title || !this.form.content){
        this.$message.error('请提前写完信息！');
      }else{
        tempCreateApi(this.form).then(({ message }) => {
          this.$message.success(message)
          this.dialogVisible = false
          this.getList()
        })
          .catch(({ message }) => {
            this.$message.error(message)
          })
      }
    }
  },

}
