import {
  userLstApi,
  changeGroupApi,
  changelabelApi,
  changeNowMoneyApi,
  changeScoreApi,
  batchChangeGroupApi,
  batchChangelabelApi,
  userUpdateApi,
  groupLstApi,
  labelLstApi
} from '@/api/user'
import newsCategory from '@/components/newsCategory/index.vue'
import userDetails from './userDetails'

export default {
  name: 'UserList',
  components: {
    newsCategory,
    userDetails
  },
  data() {
    return {
      moren: require("@/assets/images/f.png"),
      pickerOptions: {
        shortcuts: [{
          text: '今天',
          onClick(picker) {
            const end = new Date()
            const start = new Date()
            start.setTime(new Date(new Date().getFullYear(), new Date().getMonth(), new Date().getDate()))
            picker.$emit('pick', [start, end])
          }
        },
          {
            text: '昨天',
            onClick(picker) {
              const end = new Date()
              const start = new Date()
              start.setTime(start.setTime(new Date(new Date().getFullYear(), new Date().getMonth(), new Date().getDate() - 1)))
              end.setTime(end.setTime(new Date(new Date().getFullYear(), new Date().getMonth(), new Date().getDate())))
              picker.$emit('pick', [start, end])
            }
          },
          {
            text: '最近7天',
            onClick(picker) {
              const end = new Date()
              const start = new Date()
              start.setTime(start.getTime() - 3600 * 1000 * 24 * 7)
              picker.$emit('pick', [start, end])
            }
          },
          {
            text: '最近30天',
            onClick(picker) {
              const end = new Date()
              const start = new Date()
              start.setTime(start.getTime() - 3600 * 1000 * 24 * 30)
              picker.$emit('pick', [start, end])
            }
          },
          {
            text: '本月',
            onClick(picker) {
              const end = new Date()
              const start = new Date()
              start.setTime(start.setTime(new Date(new Date().getFullYear(), new Date().getMonth(), 1)))
              picker.$emit('pick', [start, end])
            }
          },
          {
            text: '本年',
            onClick(picker) {
              const end = new Date()
              const start = new Date()
              start.setTime(start.setTime(new Date(new Date().getFullYear(), 0, 1)))
              picker.$emit('pick', [start, end])
            }
          }
        ]
      },
      timeVal: [],
      collapse: false,
      visibleDetail: false,
      maxCols: 3,
      isShowSend: true,
      visible: false,
      user_type: '',
      tableData: {
        data: [],
        total: 0
      },
      listLoading: true,
      multipleSelection: [],
      ids: '',
      wechatIds: '',
      uid: '',
      labelPosition: 'right',
      userProps: {
        children: 'children',
        label: 'name',
        value: 'name'
      },
      userFrom: {
        label_id: '',
        user_type: '',
        sex: '',
        is_promoter: '',
        country: '',
        pay_count: '',
        user_time_type: '',
        user_time: '',
        nickname: '',
        real_name: '',
        card_id: '',
        province: '',
        city: '',
        page: 1,
        limit: 20,
        group_id: ''
      },
      address: [],
      grid: {
        xl: 8,
        lg: 12,
        md: 12,
        sm: 24,
        xs: 24
      },
      grid2: {
        xl: 18,
        lg: 16,
        md: 12,
        sm: 24,
        xs: 24
      },
      grid3: {
        xl: 8,
        lg: 12,
        md: 12,
        sm: 24,
        xs: 24
      },
      addresData: [],
      groupList: [],
      labelLists: []
    }
  },
  mounted() {
    this.groupLists()
    this.getTagList()
    this.getList('')
  },
  methods: {

    // 选择时间
    selectChange(tab) {
      this.timeVal = []
      this.userFrom.user_time = tab
      this.getList('')
    },
    // 具体日期
    onchangeTime(e) {
      this.timeVal = e
      this.userFrom.user_time = e ? this.timeVal.join('-') : ''
    },
    userSearchs() {
      if (this.userFrom.user_time_type && (!this.userFrom.user_time)) {
        this.$message.error('请选择访问时间')
      } else if (!this.userFrom.user_time_type && (this.userFrom.user_time)) {
        this.$message.error('请选择访问情况')
      } else {
        this.getList(1)
      }
    },

    // 分组列表
    groupLists() {
      groupLstApi({
        page: 1,
        limit: 9999
      }).then(async res => {
        this.groupList = res.data.list
      }).catch(res => {
        this.$message.error(res.message)
      })
    },
    // 标签列表
    getTagList() {
      labelLstApi({
        page: 1,
        limit: 9999
      }).then(res => {
        this.labelLists = res.data.list
      }).catch(res => {
        this.$message.error(res.message)
      })
    },
    // 账户详情
    onDetails(id) {
      this.uid = id
      this.visibleDetail = true
    },
    Close() {
      this.visibleDetail = false
    },
    sendNews() {
      if (this.multipleSelection.length === 0) return this.$message.warning('请先选择用户')
      this.visible = true
    },
    handleClose() {
      this.visible = false
    },
    handleSelectionChange(val) {
      this.multipleSelection = val
      const data = []
      const wechatData = []
      this.multipleSelection.map((item) => {
        data.push(item.uid)
        wechatData.push(item.wechat_user_id)
      })
      this.ids = data.join(',')
      this.wechatIds = wechatData.join(',')
    },
    // 修改分组
    setGroup(row) {
      this.$modalForm(changeGroupApi(row.uid))
    },
    // 批量分组
    batchGroup() {
      if (this.multipleSelection.length === 0) return this.$message.warning('请先选择用户')
      this.$modalForm(batchChangeGroupApi({
        ids: this.ids
      }))
    },
    // 修改标签
    setLabel(row) {
      this.$modalForm(changelabelApi(row.uid))
    },
    // 批量标签
    batchlabel() {
      if (this.multipleSelection.length === 0) return this.$message.warning('请先选择用户')
      this.$modalForm(batchChangelabelApi({
        ids: this.ids
      }))
    },
    // 修改余额
    setMoney(row) {
      this.$modalForm(changeNowMoneyApi(row.uid))
    },
    // 修改积分
    setScore(row) {
      this.$modalForm(changeScoreApi(row.uid))
    },
    // 列表
    getList(num) {
      this.listLoading = true
      this.userFrom.page = num ? num : this.userFrom.page;
      this.userFrom.user_type = this.user_type
      this.userFrom.province = this.address[0]
      this.userFrom.city = this.address[1]
      if (this.userFrom.user_type === '0') this.userFrom.user_type = ''
      userLstApi(this.userFrom).then(res => {
        this.tableData.data = res.data.list
        this.tableData.total = res.data.count
        this.listLoading = false
      }).catch(res => {
        this.listLoading = false
        this.$message.error(res.message)
      })
    },
    pageChange(page) {
      this.userFrom.page = page
      this.getList('')
    },
    handleSizeChange(val) {
      this.userFrom.limit = val
      this.getList('')
    },
    // 编辑
    onEdit(id) {
      this.$modalForm(userUpdateApi(id)).then(() => this.getList(''))
    },
    // 重置
    reset() {
      this.userFrom = {
        label_id: '',
        user_type: '',
        sex: '',
        is_promoter: '',
        country: '',
        pay_count: '',
        user_time_type: '',
        user_time: '',
        nickname: '',
        card_id: '',
        real_name: '',
        province: '',
        city: '',
        page: 1,
        limit: 20,
        group_id: ''
      }
      this.timeVal = []
    }
  }
}
