} from "@/api/integralState";
import {fromList} from "@/libs/constants.js";

export default {
  name: "merchant",
  data() {
    return {
      fromList: fromList,
      tableData: {
        data: [],
        total: 0,
      },
      listLoading: true,
      tableFrom: {
        page: 1,
        limit: 20,
        date: "",
        mer_name: "",
