import {
  broadcastListApi,
  changeReplayApi,
  changeDisplayApi,
  broadcastDeleteApi,
  broadcastAuditApi,
} from "@/api/marketing";
import detailsFrom from "./studioDetail";
import { roterPre } from "@/settings";
export default {
  name: "StudioList",
  components: { detailsFrom },
  data() {
    return {
      Loading: false,
      dialogVisible: false,
      broadcast_room_id: 0,
      roterPre: roterPre,
      listLoading: true,
      tableData: {
        data: [],
        total: 0,
      },
      tableFrom: {
        page: 1,
        limit: 20,
        status_tag: "",
        is_trader: ''
      },
    };
  },
  mounted() {
    this.getList();
  },
  methods: {
    // 删除
    handleDelete(item, idx) {
      (item.status == 2 && item.live_status == 101) ?
        this.$confirm('该直播间正在进行直播，删除后不可恢复，您确认删除吗？', '提示', {
          confirmButtonText: '删除',
          cancelButtonText: '不删除',
          type: 'warning'
        }).then(() => {
          broadcastDeleteApi(item.broadcast_room_id)
            .then(({ message }) => {
              this.$message.success(message);
              this.tableData.data.splice(idx, 1)
            })
            .catch(({ message }) => {
              this.$message.error(message);
            });
        }).catch(action => {
          this.$message({
            type: 'info',
            message: '已取消'
          })
        })
        :
      this.$modalSureDelete().then(() => {
        broadcastDeleteApi(item.broadcast_room_id)
          .then(({ message }) => {
            this.$message.success(message)
            this.tableData.data.splice(idx, 1)
          })
          .catch(({ message }) => {
            this.$message.error(message)
          })
      })
    },
    // 详情
    onStudioDetails(id) {
      this.broadcast_room_id = id
      this.$refs.studioDetail.dialogVisible = true
      this.$refs.studioDetail.isEdit = false
      this.$refs.studioDetail.getData(id)
    },
    // 编辑
    handleEdit(id) {
      this.broadcast_room_id = id
      this.$refs.studioDetail.dialogVisible = true
      this.$refs.studioDetail.isEdit = true
      this.$refs.studioDetail.getData(id)
    },
    // 列表
    getList(num) {
      this.listLoading = true;
      this.tableFrom.page = num ? num : this.tableFrom.page
      broadcastListApi(this.tableFrom)
        .then((res) => {
          this.tableData.data = res.data.list
          this.tableData.total = res.data.count
          this.listLoading = false;
        })
        .catch((res) => {
          this.listLoading = false
          this.$message.error(res.message)
        })
    },
    pageChange(page) {
      this.tableFrom.page = page;
      this.getList();
    },
    handleSizeChange(val) {
      this.tableFrom.limit = val;
      this.getList();
    },
    // 审核
    handleAudit(id) {
      this.$modalForm(broadcastAuditApi(id)).then(() => this.getList());
    },
    // 修改显示状态
    onchangeIsShow(row) {
      changeDisplayApi(row.broadcast_room_id, { is_show: row.is_show })
        .then(({ message }) => {
          this.$message.success(message);
          this.getList();
        })
        .catch(({ message }) => {
          this.$message.error(message);
        });
    },

  },
};
